query:
SELECT 'Orders customerIds' as source, GROUP_CONCAT(DISTINCT customerId ORDER BY customerId) as ids FROM orders
UNION ALL
SELECT 'Users IDs' as source, GROUP_CONCAT(id ORDER BY id SEPARATOR ',') as ids FROM users WHERE id <= 20;

